import { LitElement, css, html } from 'lit';

export class soundComponent extends LitElement {
static properties = {
    }

    constructor() {
        super();
    }

    static styles = css`
    #footer {
        background-color: #edeced;
        display: flex;
        align-items: left;
    }
    .box {
        text-align: center;
        padding: 10px;
        color: #2d2d2d;
      }
    `;

    connectedCallback() {
        super.connectedCallback();
    }

    render() {
        return html`
        <audio id="alarm">
        <source src="./sounds/fairy.mp3" type="audio/mpeg">
        </audio>
        `;
    }

    alarm() {
        const alarmS = this.shadowRoot.querySelector("#alarm");
        console.log(alarmS);
        alarmS.play();
    }

  
}
customElements.define('sound-component', soundComponent);

